import { useState, useEffect } from "react";
import { getPlants, addPlant, deletePlant } from "../services/plants";
import { getReminders } from "../services/reminders";

export default function Plants() {
  const [plants, setPlants] = useState([]);
  const [reminders, setReminders] = useState([]);
  const [name, setName] = useState("");
  const [type, setType] = useState("");

  useEffect(() => {
    cargarPlantas();
    cargarReminders();
  }, []);

  const cargarPlantas = async () => {
    const data = await getPlants();
    setPlants(data);
  };

  const cargarReminders = async () => {
    const data = await getReminders();
    setReminders(data);
  };

  const agregar = async () => {
    await addPlant({ name, type, last_watered: new Date() });
    setName("");
    setType("");
    cargarPlantas();
  };

  const eliminar = async (id) => {
    // ¿Tiene recordatorios?
    const recordatoriosDePlanta = reminders.filter(r => r.plantId == id);
    if (recordatoriosDePlanta.length > 0) {
      alert("No puedes eliminar esta planta porque tiene recordatorios asociados. Elimina los recordatorios primero.");
      return;
    }
    await deletePlant(id);
    cargarPlantas();
  };

 // ...importaciones y lógica igual, solo cambia el return...
return (
  <div style={{
    maxWidth: 600, margin: "60px auto", background: "#fff",
    borderRadius: 20, boxShadow: "0 2px 16px #38764c18", padding: "40px 28px"
  }}>
    <h2 style={{ color: "#38764c" }}>Mis plantas</h2>
    <div style={{ display: "flex", gap: 14, marginBottom: 30 }}>
      <input placeholder="Nombre" value={name} onChange={e => setName(e.target.value)} style={inputEstilo()} />
      <input placeholder="Tipo" value={type} onChange={e => setType(e.target.value)} style={inputEstilo()} />
      <button onClick={agregar} style={btnEstilo({ minWidth: 90 })}>Agregar</button>
    </div>
    <ul style={{ padding: 0, listStyle: "none" }}>
      {plants.map(p => (
        <li key={p.id} style={{
          margin: "0 0 14px", padding: "12px 20px", borderRadius: 10, background: "#e2f5e7", color: "#226436",
          display: "flex", justifyContent: "space-between", alignItems: "center"
        }}>
          <span>
            <b>{p.name}</b> <span style={{ color: "#37744b" }}>({p.type})</span>
          </span>
          <button onClick={() => eliminar(p.id)} style={btnEstilo({ background: "#e04343", color: "#fff", padding: "6px 14px", fontSize: 15 })}>Eliminar</button>
        </li>
      ))}
    </ul>
  </div>
);

}
