import { useState } from "react";
import axios from "axios";

export default function Support() {
  const [email, setEmail] = useState("");
  const [msg, setMsg] = useState("");
  const [success, setSuccess] = useState("");
  const [error, setError] = useState("");

  const enviar = async (e) => {
    e.preventDefault();
    setSuccess("");
    setError("");
    try {
      await axios.post("http://localhost:5000/api/support", {
        email,
        message: msg
      });
      setSuccess("¡Mensaje enviado! Te responderemos pronto.");
      setEmail("");
      setMsg("");
    } catch (err) {
      setError("Error al enviar mensaje. Intenta más tarde.");
    }
  };

  return (
    <div style={{ maxWidth: 500, margin: "60px auto", textAlign: "center" }}>
      <h2>Soporte Plantcare</h2>
      {/* Aquí puedes poner info/FAQ si quieres */}
      <hr />
      <h4>¿Quieres contactarnos directamente?</h4>
      <form onSubmit={enviar}>
        <input
          type="email"
          placeholder="Tu correo"
          value={email}
          onChange={e => setEmail(e.target.value)}
          required
        /><br />
        <textarea
          placeholder="Tu mensaje"
          value={msg}
          onChange={e => setMsg(e.target.value)}
          required
          style={{ width: "80%", margin: "10px 0" }}
        />
        <br />
        <button type="submit">Enviar mensaje</button>
      </form>
      {success && <p style={{ color: "green" }}>{success}</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}
    </div>
  );
}
