import { useEffect, useState } from "react";
import axios from "axios";

const API = "http://localhost:5000/api/community";
const token = () => localStorage.getItem("token");

export default function Community() {
  const [posts, setPosts] = useState([]);
  const [message, setMessage] = useState("");
  const [image, setImage] = useState(null);

  useEffect(() => {
    cargarPosts();
  }, []);

  const cargarPosts = async () => {
    try {
      const res = await axios.get(API);
      setPosts(res.data);
    } catch (err) {
      alert("Error al cargar comunidad");
    }
  };

  const publicar = async () => {
    if (!message.trim()) return;
    try {
      const formData = new FormData();
      formData.append("message", message);
      if (image) formData.append("image", image);

      await axios.post(API, formData, {
        headers: {
          Authorization: token(),
          "Content-Type": "multipart/form-data"
        }
      });
      setMessage("");
      setImage(null);
      cargarPosts();
    } catch (err) {
      alert("Error al publicar (¿estás logueado?)");
    }
  };

  return (
    <div style={{ maxWidth: 500, margin: "40px auto", textAlign: "center" }}>
      <h2>Comunidad Plantcare</h2>
      <div>
        <textarea
          value={message}
          onChange={e => setMessage(e.target.value)}
          placeholder="¡Miren cómo va mi planta!"
          rows={3}
          style={{ width: "90%" }}
        />
        <br />
        <input
          type="file"
          accept="image/*"
          onChange={e => setImage(e.target.files[0])}
        />
        <br />
        <button onClick={publicar}>Publicar</button>
      </div>
      <hr />
      <ul style={{ listStyle: "none", padding: 0 }}>
        {posts.map(p => (
          <li key={p.id} style={{ marginBottom: 20, textAlign: "left", borderBottom: "1px solid #ccc", paddingBottom: 10 }}>
            <b>{p.userName}</b> <span style={{ color: "#888", fontSize: 12 }}>{new Date(p.created_at).toLocaleString()}</span>
            <br />
            {p.message}
            {p.image &&
              <div>
                <img
                  src={`http://localhost:5000/uploads/${p.image}`}
                  alt="Imagen planta"
                  style={{ maxWidth: 250, marginTop: 10, borderRadius: "1em" }}
                />
              </div>
            }
          </li>
        ))}
      </ul>
    </div>
  );
}
