import { useState, useEffect } from "react";
import { getReminders, addReminder, updateReminder, deleteReminder } from "../services/reminders";

export default function Reminders() {
  const [reminders, setReminders] = useState([]);
  const [note, setNote] = useState("");
  const [remindAt, setRemindAt] = useState("");
  const [plantId, setPlantId] = useState("");
  const [editing, setEditing] = useState(null);

  useEffect(() => {
    cargar();
  }, []);

  const cargar = async () => {
    const data = await getReminders();
    setReminders(data);
  };

  const guardar = async () => {
    if (editing) {
      await updateReminder(editing, { note, remind_at: remindAt });
      setEditing(null);
    } else {
      if (!note || !remindAt || !plantId) {
  alert("Todos los campos son obligatorios");
  return;
}

      await addReminder({ note, remind_at: remindAt, plantId });
      
    }
    setNote("");
    setRemindAt("");
    setPlantId("");
    cargar();
  };

  const editar = (reminder) => {
    setNote(reminder.note);
    setRemindAt(reminder.remind_at?.slice(0, 16)); // Para input tipo datetime-local
    setPlantId(reminder.plantId);
    setEditing(reminder.id);
  };

  const eliminar = async (id) => {
    await deleteReminder(id);
    cargar();
  };

  return (
    <div style={{ textAlign: "center" }}>
      <h2>Recordatorios</h2>
      <input
        placeholder="Nota"
        value={note}
        onChange={e => setNote(e.target.value)}
      />
      <input
        type="datetime-local"
        value={remindAt}
        onChange={e => setRemindAt(e.target.value)}
      />
      <input
        placeholder="ID Planta"
        value={plantId}
        onChange={e => setPlantId(e.target.value)}
      />
      <button onClick={guardar}>{editing ? "Actualizar" : "Agregar"}</button>
      <ul>
        {reminders.map(r => (
          <li key={r.id}>
            {r.note} â€” {r.remind_at && new Date(r.remind_at).toLocaleString()} (Planta: {r.plantId})
            <button onClick={() => editar(r)} style={{ marginLeft: 10 }}>Editar</button>
            <button onClick={() => eliminar(r.id)} style={{ marginLeft: 10 }}>Eliminar</button>
          </li>
        ))}
      </ul>
    </div>
  );
}
