import { useState, useEffect } from "react";
import axios from "axios";

export default function Community() {
  const [posts, setPosts] = useState([]);
  const [message, setMessage] = useState("");
  const [image, setImage] = useState(null);

  useEffect(() => {
    cargarPosts();
  }, []);

  const cargarPosts = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/community");
      setPosts(res.data);
    } catch (err) {
      alert("Error al cargar comunidad");
    }
  };

  const publicar = async () => {
    if (!message.trim()) return;
    try {
      const formData = new FormData();
      formData.append("message", message);
      if (image) formData.append("image", image);

      await axios.post("http://localhost:5000/api/community", formData, {
        headers: {
          "Content-Type": "multipart/form-data"
        }
      });
      setMessage("");
      setImage(null);
      cargarPosts();
    } catch (err) {
      alert("Error al publicar (Â¿estÃ¡s logueado?)");
    }
  };

  return (
    <div style={{
      maxWidth: 650, margin: "50px auto", background: "#fff", borderRadius: 20,
      boxShadow: "0 2px 14px #38764c12", padding: "34px 22px"
    }}>
      <h2 style={{ color: "#38764c" }}>Comunidad Plantcare ðŸŒ±</h2>
      <div style={{ marginBottom: 20 }}>
        <textarea value={message} onChange={e => setMessage(e.target.value)}
          placeholder="Â¡Miren cÃ³mo va mi planta!"
          rows={3} style={{
            width: "95%", padding: 10, borderRadius: 9, border: "1px solid #cbead7",
            fontSize: 16, resize: "vertical"
          }}
        /><br />
        <input type="file" accept="image/*" onChange={e => setImage(e.target.files[0])}
          style={{ margin: "10px 0" }} />
        <br />
        <button onClick={publicar} style={btnEstilo()}>Publicar</button>
      </div>
      <hr />
      <ul style={{ listStyle: "none", padding: 0 }}>
        {posts.map(p => (
          <li key={p.id} style={{
            marginBottom: 22, background: "#e2f5e7", borderRadius: 14,
            padding: 15, boxShadow: "0 1px 6px #38764c13"
          }}>
            <b>{p.userName}</b>{" "}
            <span style={{ color: "#888", fontSize: 13 }}>{new Date(p.created_at).toLocaleString()}</span>
            <br />
            <span>{p.message}</span>
            {p.image && (
              <div>
                <img src={`http://localhost:5000/uploads/${p.image}`}
                  alt="Imagen planta" style={{
                    maxWidth: 250, marginTop: 10, borderRadius: "1em", border: "2px solid #b8e2c1"
                  }} />
              </div>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}

function btnEstilo() {
  return {
    background: "#38764c",
    color: "#fff",
    fontWeight: "bold",
    fontSize: 16,
    padding: "12px 24px",
    border: "none",
    borderRadius: "8px",
    cursor: "pointer",
    marginTop: 12,
    boxShadow: "0 1px 5px #38764c25",
    transition: "background 0.2s",
  };
}
