import { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

export default function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("http://localhost:5000/api/auth/login", {
        email, password
      });
      localStorage.setItem("token", res.data.token);
      navigate("/dashboard");
    } catch (err) {
      alert("Credenciales incorrectas");
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ marginTop: 80, textAlign: "center" }}>
      <h2>Iniciar sesión</h2>
      <input placeholder="Correo" value={email} onChange={e => setEmail(e.target.value)} /><br />
      <input placeholder="Contraseña" type="password" value={password} onChange={e => setPassword(e.target.value)} /><br />
      <button type="submit">Entrar</button>
      <br />
      <button onClick={() => navigate("/register")} type="button">¿No tienes cuenta? Regístrate</button>
    </form>
  );
}
