import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

export default function Reminders() {
  const [reminders, setReminders] = useState([]);
  const [note, setNote] = useState("");
  const [remindAt, setRemindAt] = useState("");
  const [plantId, setPlantId] = useState("");
  const [editing, setEditing] = useState(null);
  const [plants, setPlants] = useState([]);
  const navigate = useNavigate(); // Usamos useNavigate para regresar a la página anterior

  // Cargar recordatorios y plantas
  useEffect(() => {
    cargarReminders();
    cargarPlantas();
  }, []);

  const cargarReminders = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/reminders");
      setReminders(res.data);
    } catch (err) {
      console.error("Error al cargar recordatorios:", err);
    }
  };

  const cargarPlantas = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/plants");
      setPlants(res.data);
    } catch (err) {
      console.error("Error al cargar plantas:", err);
    }
  };

  const guardar = async () => {
    if (!note || !remindAt || !plantId) {
      alert("Todos los campos son obligatorios");
      return;
    }

    try {
      if (editing) {
        await axios.put(`http://localhost:5000/api/reminders/${editing}`, { note, remind_at: remindAt, plantId });
        setEditing(null);
      } else {
        await axios.post("http://localhost:5000/api/reminders", { note, remind_at: remindAt, plantId });
      }
      setNote("");
      setRemindAt("");
      setPlantId("");
      cargarReminders();
    } catch (err) {
      console.error("Error al guardar el recordatorio:", err);
      alert("Hubo un error al guardar el recordatorio");
    }
  };

  const editar = (reminder) => {
    setNote(reminder.note);
    setRemindAt(reminder.remind_at?.slice(0, 16)); // Para input tipo datetime-local
    setPlantId(reminder.plantId);
    setEditing(reminder.id);
  };

  const eliminar = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/reminders/${id}`);
      cargarReminders();
    } catch (err) {
      console.error("Error al eliminar el recordatorio:", err);
      alert("Hubo un error al eliminar el recordatorio");
    }
  };

  const goBack = () => {
    navigate(-1); // Regresa a la página anterior en el historial
  };

  return (
    <div style={{
      maxWidth: 600, margin: "60px auto", background: "#fff",
      borderRadius: 20, boxShadow: "0 2px 16px #38764c18", padding: "40px 28px"
    }}>
      {/* Botón de regreso */}
      <button
        onClick={goBack}
        style={{
          background: "#38764c",
          color: "#fff",
          fontSize: "16px",
          padding: "12px 24px",
          border: "none",
          borderRadius: "8px",
          cursor: "pointer",
          boxShadow: "0 2px 8px rgba(0, 0, 0, 0.2)",
          display: "flex",
          alignItems: "center",
          gap: "8px",
          marginBottom: "20px"
        }}
      >
        {/* Ícono de flecha */}
        <svg
          xmlns="http://www.w3.org/2000/svg"
          fill="none"
          viewBox="0 0 24 24"
          stroke="currentColor"
          width="20"
          height="20"
          style={{ marginRight: "8px" }}
        >
          <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth="2"
            d="M15 19l-7-7 7-7"
          />
        </svg>
        Volver
      </button>

      <h2 style={{ color: "#38764c" }}>Recordatorios</h2>
      <div style={{ display: "flex", gap: 12, marginBottom: 20 }}>
        <input
          placeholder="Nota"
          value={note}
          onChange={e => setNote(e.target.value)}
          style={inputEstilo()}
        />
        <input
          type="datetime-local"
          value={remindAt}
          onChange={e => setRemindAt(e.target.value)}
          style={inputEstilo()}
        />
        <select
          value={plantId}
          onChange={e => setPlantId(e.target.value)}
          style={inputEstilo()}
        >
          <option value="">Selecciona una planta</option>
          {plants.map(plant => (
            <option key={plant.id} value={plant.id}>{plant.name}</option>
          ))}
        </select>
        <button onClick={guardar} style={btnEstilo()}>
          {editing ? "Actualizar" : "Agregar"}
        </button>
      </div>
      <ul style={{ padding: 0, listStyle: "none" }}>
        {reminders.map(r => (
          <li key={r.id} style={{
            margin: "0 0 14px", padding: "12px 20px", borderRadius: 10, background: "#e2f5e7", color: "#226436",
            display: "flex", justifyContent: "space-between", alignItems: "center"
          }}>
            <span>
              {r.note} <span style={{ color: "#37744b" }}>({plants.find(p => p.id == r.plantId)?.name || r.plantId})</span>
              <span style={{ color: "#999", marginLeft: 12 }}>{r.remind_at && new Date(r.remind_at).toLocaleString()}</span>
            </span>
            <div>
              <button onClick={() => editar(r)} style={btnEstilo({ background: "#94c661", color: "#fff", padding: "6px 14px", fontSize: 15, marginRight: 6 })}>
                Editar
              </button>
              <button onClick={() => eliminar(r.id)} style={btnEstilo({ background: "#e04343", color: "#fff", padding: "6px 14px", fontSize: 15 })}>
                Eliminar
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

function inputEstilo() {
  return {
    width: "100%",
    margin: "10px 0",
    padding: "12px",
    border: "1px solid #cbead7",
    borderRadius: 10,
    fontSize: 16
  };
}

function btnEstilo(extra = {}) {
  return {
    background: "#38764c",
    color: "#fff",
    fontWeight: "bold",
    fontSize: 16,
    padding: "10px 24px",
    border: "none",
    borderRadius: "8px",
    cursor: "pointer",
    marginTop: 18,
    boxShadow: "0 1px 5px #38764c25",
    transition: "background 0.2s",
    ...extra
  };
}
