import { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import Swal from "sweetalert2";

export default function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!email || !password) {
      Swal.fire({
        icon: "warning",
        title: "Faltan datos",
        text: "Por favor, ingresa tu correo y contraseña.",
        confirmButtonColor: "#38764c"
      });
      return;
    }

    try {
      const res = await axios.post("http://localhost:5000/api/auth/login", {
        email,
        password,
      });
      localStorage.setItem("token", res.data.token);

      Swal.fire({
        icon: "success",
        title: "Bienvenido",
        text: "¡Has iniciado sesión correctamente!",
        confirmButtonColor: "#38764c",
        timer: 1800,
        showConfirmButton: false,
        background: "#e2f5e7",
        color: "#38764c"
      });

      setTimeout(() => {
        navigate("/dashboard");
      }, 1500);

    } catch (err) {
      Swal.fire({
        icon: "error",
        title: "Credenciales incorrectas",
        text: "Verifica tu correo y contraseña.",
        confirmButtonColor: "#38764c"
      });
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{
      background: "#fff",
      maxWidth: 370,
      margin: "90px auto",
      borderRadius: "1.5em",
      boxShadow: "0 2px 10px #38764c22",
      padding: "32px 26px",
      textAlign: "center"
    }}>
      <h2 style={{ color: "#38764c" }}>Iniciar sesión</h2>
      <input
        placeholder="Correo"
        type="email"
        value={email}
        onChange={e => setEmail(e.target.value)}
        style={inputEstilo()}
        required
      /><br />

      <div style={{ position: "relative" }}>
        <input
          placeholder="Contraseña"
          type={showPassword ? "text" : "password"}
          value={password}
          onChange={e => setPassword(e.target.value)}
          style={inputEstilo()}
          required
        />
        <button
          type="button"
          onClick={() => setShowPassword(!showPassword)}
          style={{
            position: "absolute",
            top: "50%",
            right: "10px",
            transform: "translateY(-50%)",
            background: "transparent",
            border: "none",
            cursor: "pointer",
            color: "#38764c",
            fontWeight: "bold"
          }}
        >
          {showPassword ? "Ocultar" : "Mostrar"}
        </button>
      </div><br />

      <button type="submit" style={btnEstilo()}>Entrar</button>
      <button
        onClick={() => navigate("/register")}
        type="button"
        style={{ ...btnEstilo({ background: "#52a96b", marginTop: 12 }) }}>
        ¿No tienes cuenta? Regístrate
      </button>
    </form>
  );
}

function inputEstilo() {
  return {
    width: "100%",
    margin: "10px 0",
    padding: "12px",
    border: "1px solid #cbead7",
    borderRadius: 10,
    fontSize: 16
  };
}

function btnEstilo(extra = {}) {
  return {
    background: "#38764c",
    color: "#fff",
    fontWeight: "bold",
    fontSize: 16,
    padding: "10px 24px",
    border: "none",
    borderRadius: "8px",
    cursor: "pointer",
    marginTop: 18,
    boxShadow: "0 1px 5px #38764c25",
    transition: "background 0.2s",
    ...extra
  };
}
