import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { getPlants, addPlant, deletePlant } from "../services/plants";
import { getReminders } from "../services/reminders";

export default function Plants() {
  const [plants, setPlants] = useState([]);
  const [name, setName] = useState("");
  const [type, setType] = useState("");
  const [editing, setEditing] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    cargarPlantas();
  }, []);

  const cargarPlantas = async () => {
    setLoading(true);
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get("http://localhost:5000/api/plants", {
        headers: { Authorization: `Bearer ${token}` }
      });
      setPlants(res.data);
    } catch (err) {
      console.error("Error al cargar las plantas:", err);
      alert("Por favor inicia sesiÃ³n para ver tus plantas");
    }
    setLoading(false);
  };

  const agregar = async () => {
    if (!name || !type) {
      alert("Por favor ingresa el nombre y el tipo de la planta");
      return;
    }
    try {
      const token = localStorage.getItem("token");
      if (editing) {
        await axios.put(
          `http://localhost:5000/api/plants/${editing}`,
          { name, type },
          { headers: { Authorization: `Bearer ${token}` } }
        );
        setEditing(null);
      } else {
        await axios.post(
          "http://localhost:5000/api/plants",
          { name, type },
          { headers: { Authorization: `Bearer ${token}` } }
        );
      }
      setName("");
      setType("");
      cargarPlantas();
    } catch (err) {
      console.error("Error al agregar la planta:", err);
      alert("Hubo un error al guardar la planta");
    }
  };

  const eliminar = async (id) => {
    try {
      const token = localStorage.getItem("token");
      await axios.delete(`http://localhost:5000/api/plants/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      cargarPlantas();
    } catch (err) {
      console.error("Error al eliminar la planta:", err);
      alert(
        err.response?.data?.msg ||
          "Hubo un error al eliminar la planta"
      );
    }
  };

  const editar = (plant) => {
    setName(plant.name);
    setType(plant.type);
    setEditing(plant.id);
  };

  const goBack = () => {
    navigate(-1);
  };

  return (
    <div style={{
      maxWidth: 600, margin: "60px auto", background: "#fff",
      borderRadius: 20, boxShadow: "0 2px 16px #38764c18", padding: "40px 28px"
    }}>
      {/* BotÃ³n de regreso */}
      <button
        onClick={goBack}
        style={{
          background: "#38764c",
          color: "#fff",
          fontSize: "16px",
          padding: "12px 24px",
          border: "none",
          borderRadius: "8px",
          cursor: "pointer",
          boxShadow: "0 2px 8px rgba(0, 0, 0, 0.2)",
          display: "flex",
          alignItems: "center",
          gap: "8px",
          marginBottom: "20px"
        }}
      >
        {/* Ãcono de flecha */}
        <svg
          xmlns="http://www.w3.org/2000/svg"
          fill="none"
          viewBox="0 0 24 24"
          stroke="currentColor"
          width="20"
          height="20"
          style={{ marginRight: "8px" }}
        >
          <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth="2"
            d="M15 19l-7-7 7-7"
          />
        </svg>
        Volver
      </button>

      <h2 style={{ color: "#38764c" }}>Mis plantas</h2>
      {loading ? (
        <div style={{ color: "#38764c", margin: "24px 0" }}>Cargando plantas...</div>
      ) : (
        <>
          <div style={{ display: "flex", gap: 14, marginBottom: 30 }}>
            <input
              placeholder="Nombre"
              value={name}
              onChange={e => setName(e.target.value)}
              style={inputEstilo()}
            />
            <input
              placeholder="Tipo"
              value={type}
              onChange={e => setType(e.target.value)}
              style={inputEstilo()}
            />
            <button onClick={agregar} style={btnEstilo({ minWidth: 90 })}>
              {editing ? "Actualizar" : "Agregar"}
            </button>
          </div>
          <ul style={{ padding: 0, listStyle: "none" }}>
            {plants.map(p => (
              <li key={p.id} style={{
                margin: "0 0 14px", padding: "12px 20px", borderRadius: 10, background: "#e2f5e7", color: "#226436",
                display: "flex", justifyContent: "space-between", alignItems: "center"
              }}>
                <span>
                  <b>{p.name}</b> <span style={{ color: "#37744b" }}>({p.type})</span>
                </span>
                <div>
                  <button onClick={() => editar(p)} style={btnEstilo({ background: "#94c661", color: "#fff", padding: "6px 14px", fontSize: 15, marginRight: 6 })}>
                    Editar
                  </button>
                  <button onClick={() => eliminar(p.id)} style={btnEstilo({ background: "#e04343", color: "#fff", padding: "6px 14px", fontSize: 15 })}>
                    Eliminar
                  </button>
                </div>
              </li>
            ))}
          </ul>
        </>
      )}
    </div>
  );
}

function inputEstilo() {
  return {
    width: "100%",
    margin: "10px 0",
    padding: "12px",
    border: "1px solid #cbead7",
    borderRadius: 10,
    fontSize: 16
  };
}

function btnEstilo(extra = {}) {
  return {
    background: "#38764c",
    color: "#fff",
    fontWeight: "bold",
    fontSize: 16,
    padding: "10px 24px",
    border: "none",
    borderRadius: "8px",
    cursor: "pointer",
    marginTop: 18,
    boxShadow: "0 1px 5px #38764c25",
    transition: "background 0.2s",
    ...extra
  };
}
